module DocxGenerator
	
	def comprovacio_sistema(sistema)
    referencies = Referencia.where(:edifici_id => @edifici.id, :sistema => sistema)
    if referencies.count > 0
      return true
    else
      return false
    end
  end

  def comprovacio_sistema_manual(sistema)
    referencies = Referencia.where(:edifici_id => @edifici.id, :sistema => sistema, :manual_habitatge => true)
    if referencies.count > 0
      return true
    else
      return false
    end
  end

  def titol(sistema)
  	text_titol = '<w:p></w:p><w:p w14:paraId="588ED247" w14:textId="77777777" w:rsidR="004B4C63" w:rsidRDefault="004B4C63" w:rsidP="004B4C63"><w:pPr><w:pStyle w:val="Heading2"/></w:pPr><w:r><w:t>' + sistema + '</w:t></w:r></w:p><w:p w14:paraId="7A25D5A4" w14:textId="77777777" w:rsidR="004B4C63" w:rsidRDefault="004B4C63" w:rsidP="004B4C63"/><w:p w14:paraId="1881EB58" w14:textId="77777777" w:rsidR="004B4C63" w:rsidRPr="000507D2" w:rsidRDefault="004B4C63" w:rsidP="004B4C63"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr><w:r w:rsidRPr="000507D2"><w:rPr><w:b/></w:rPr><w:t>Descripció constructiva</w:t></w:r></w:p><w:p w14:paraId="3A35BA0E" w14:textId="63CA0781" w:rsidR="004B4C63" w:rsidRPr="004B4C63" w:rsidRDefault="004B4C63" w:rsidP="004B4C63"/>'
  	return text_titol
  end

  def descripcio(sistema)
  	sistema_seleccionat = sistema.where(:edifici_id => @edifici.id).last
    #Inici del llistat
    llistat_elements = '<w:p w14:paraId="49168E24" w14:textId="77777777" w:rsidP="00C41124" w:rsidR="00C41124" w:rsidRDefault="00C41124"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs></w:pPr></w:p>'
    sistema_seleccionat.attributes.each_pair do |name, value|
      if (value == true)
        element = ElementPredefinit.where(:nom_element => name).last
        #Afegim els elements del llistat
        llistat_elements = llistat_elements + '<w:p w14:paraId="0157D4C5" w14:textId="77777777" w:rsidP="00C41124" w:rsidR="00C41124" w:rsidRDefault="00C41124"><w:pPr><w:pStyle w:val="ListParagraph1"/><w:numPr><w:ilvl w:val="0"/><w:numId w:val="1"/></w:numPr><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:spacing w:after="0"/></w:pPr><w:r><w:t>' + element.descripcio_ca + '</w:t></w:r></w:p>'
      end
    end
    #Tanquem el llistat
    llistat_elements = llistat_elements + '<w:p w14:paraId="0D2D2F09" w14:textId="77777777" w:rsidP="00C41124" w:rsidR="00C41124" w:rsidRDefault="00C41124"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs></w:pPr></w:p><w:p w14:paraId="19722511" w14:textId="77777777" w:rsidR="00500DE7" w:rsidRDefault="00500DE7"><w:bookmarkStart w:id="0" w:name="_GoBack"/><w:bookmarkEnd w:id="0"/></w:p>'
    return llistat_elements
  end

  def instruccions(sistema)
  	case sistema
  	when 'fonamentacio'
  		return '<w:p w14:paraId="401066E7" w14:textId="77777777" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4" w:rsidP="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions d’ús</w:t></w:r></w:p><w:p w14:paraId="50487858" w14:textId="77777777" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4" w:rsidP="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr></w:p><w:p w14:paraId="073DAC4D" w14:textId="77777777" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4" w:rsidP="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr><w:r><w:t>Cal evitar qualsevol tipus de canvi en el sistema de càrrega de les diferents parts de l’edifici. Si desitgeu introduir modificacions o qualsevol canvi d’ús dins l’edifici consulteu al vostre Tècnic de Capçalera.</w:t></w:r></w:p><w:p w14:paraId="6407C1C3" w14:textId="77777777" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4" w:rsidP="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr></w:p><w:p w14:paraId="18A15419" w14:textId="77777777" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4" w:rsidP="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr><w:r><w:t>Les lesions (esquerdes, desploms) als fonaments no són apreciables directament i es detecten a partir de les que apareixen a d’altres elements constructius (parets, sostres, etc.). En aquests casos, fa falta que el Tècnic de Capçalera faci un informe sobre les lesions detectades, en determini la gravetat i, si escau, la necessitat d’intervenció.</w:t></w:r></w:p><w:p w14:paraId="0023F909" w14:textId="77777777" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4" w:rsidP="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr></w:p><w:p w14:paraId="7F15795A" w14:textId="77777777" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4" w:rsidP="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr><w:r><w:t>Les alteracions d’importància efectuades als terrenys propers, com ara: noves construccions, realització de pous, túnels, vies, carreteres o reblerts de terres poden afectar la fonamentació de l’edifici. Si durant la realització dels treballs es detecten lesions, s’hauran d’estudiar i, si és el cas, es podrà exigir la seva reparació.</w:t></w:r></w:p><w:p w14:paraId="75DD5C8B" w14:textId="77777777" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4" w:rsidP="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr></w:p><w:p w14:paraId="48342978" w14:textId="77777777" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4" w:rsidP="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr><w:r><w:t xml:space="preserve">Els corrents subterranis d’aigua natural i les fuites de conduccions d’aigua o de desguassos poden ser causa d’alteracions del terreny i de descalçaments de la fonamentació. Aquests descalçaments poden produir un assentament de la zona afectada, que pot transformar-se en deterioraments importants a la resta de l’estructura. Per aquest motiu, és primordial eliminar ràpidament qualsevol tipus d’humitat que provingui del subsòl. </w:t></w:r></w:p><w:p w14:paraId="1427CCDE" w14:textId="77777777" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4" w:rsidP="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr></w:p><w:p w14:paraId="55EB262E" w14:textId="77777777" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4" w:rsidP="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr><w:r><w:t xml:space="preserve">Després de pluges fortes s’observaran les possibles humitats i el bon funcionament de les perforacions de drenatge i desguàs. </w:t></w:r></w:p><w:p w14:paraId="3924A221" w14:textId="77777777" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4" w:rsidP="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr></w:p><w:p w14:paraId="4D6CA9A6" w14:textId="77777777" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4" w:rsidP="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr></w:p><w:p w14:paraId="5CB52117" w14:textId="77777777" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4" w:rsidP="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions de manteniment</w:t></w:r></w:p><w:p w14:paraId="2BC35CC1" w14:textId="77777777" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4" w:rsidP="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr></w:p><w:p w14:paraId="00CA1316" w14:textId="77777777" w:rsidR="00500DE7" w:rsidRDefault="00500DE7"><w:bookmarkStart w:id="0" w:name="_GoBack"/><w:bookmarkEnd w:id="0"/></w:p>'
  	when 'estructura'
  		return '<w:p w14:paraId="39A61554" w14:textId="77777777" w:rsidR="00385E0F" w:rsidRDefault="00385E0F" w:rsidP="00385E0F"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions d’ús</w:t></w:r></w:p><w:p w14:paraId="338D085D" w14:textId="77777777" w:rsidR="00385E0F" w:rsidRDefault="00385E0F" w:rsidP="00385E0F"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr></w:p><w:p w14:paraId="4486B140" w14:textId="77777777" w:rsidR="00385E0F" w:rsidRDefault="00385E0F" w:rsidP="00385E0F"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr><w:r><w:t>Les humitats persistents als elements estructurals tenen un efecte nefast sobre la conservació de l’estructura. Cal reparar-les immediatament.</w:t></w:r></w:p><w:p w14:paraId="7E4AB971" w14:textId="77777777" w:rsidR="00385E0F" w:rsidRDefault="00385E0F" w:rsidP="00385E0F"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr></w:p><w:p w14:paraId="13B9120C" w14:textId="77777777" w:rsidR="00385E0F" w:rsidRDefault="00385E0F" w:rsidP="00385E0F"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr><w:r><w:t>Si s’han de penjar objectes (quadres, prestatgeries, mobles o lluminàries) als elements estructurals cal utilitzar tacs i cargols adequats per al material de base.</w:t></w:r></w:p><w:p w14:paraId="3DF622B6" w14:textId="77777777" w:rsidR="00385E0F" w:rsidRDefault="00385E0F" w:rsidP="00385E0F"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr></w:p><w:p w14:paraId="269E7D7B" w14:textId="77777777" w:rsidR="00385E0F" w:rsidRDefault="00385E0F" w:rsidP="00385E0F"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr><w:r><w:t>Els elements que formen part de l’estructura de l’edifici, parets de càrrega incloses, no es poden alterar sense el control del Tècnic de Capçalera. Aquesta prescripció inclou la realització de regates a les parets de càrrega i l’obertura de passos per a la redistribució d’espais interiors.</w:t></w:r></w:p><w:p w14:paraId="29409799" w14:textId="77777777" w:rsidR="00385E0F" w:rsidRDefault="00385E0F" w:rsidP="00385E0F"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr></w:p><w:p w14:paraId="2E04A01A" w14:textId="77777777" w:rsidR="00385E0F" w:rsidRDefault="00385E0F" w:rsidP="00385E0F"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr><w:r><w:t>Durant la vida útil de l’edifici poden aparèixer símptomes de lesions a l’estructura o als elements en contacte amb ella. En general, aquests defectes poden tenir caràcter greu. En aquests casos, és necessari que el vostre Tècnic de Capçalera analitzi les lesions detectades, en determini la importància i, si escau, decideixi la necessitat d’una intervenció.</w:t></w:r></w:p><w:p w14:paraId="027EBEEB" w14:textId="77777777" w:rsidR="00385E0F" w:rsidRDefault="00385E0F" w:rsidP="00385E0F"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr></w:p><w:p w14:paraId="536EFD0F" w14:textId="77777777" w:rsidR="00385E0F" w:rsidRDefault="00385E0F" w:rsidP="00385E0F"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr><w:r><w:t>Relació orientativa de símptomes de lesions amb repercussió possible sobre l’estructura:</w:t></w:r></w:p><w:p w14:paraId="63EF116D" w14:textId="77777777" w:rsidR="00385E0F" w:rsidRDefault="00385E0F" w:rsidP="00385E0F"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr></w:p><w:p w14:paraId="657C5F6F" w14:textId="77777777" w:rsidR="00385E0F" w:rsidRDefault="00385E0F" w:rsidP="00385E0F"><w:pPr><w:pStyle w:val="ListParagraph1"/><w:numPr><w:ilvl w:val="0"/><w:numId w:val="1"/></w:numPr><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs><w:spacing w:after="0"/><w:rPr><w:rFonts w:ascii="Calibri" w:hAnsi="Calibri"/><w:sz w:val="24"/><w:szCs w:val="24"/></w:rPr></w:pPr><w:r><w:rPr><w:rFonts w:ascii="Calibri" w:hAnsi="Calibri"/><w:sz w:val="24"/><w:szCs w:val="24"/></w:rPr><w:t>Deformacions: desploms de parets, façanes i pilars.</w:t></w:r></w:p><w:p w14:paraId="6F66012F" w14:textId="77777777" w:rsidR="00385E0F" w:rsidRDefault="00385E0F" w:rsidP="00385E0F"><w:pPr><w:pStyle w:val="ListParagraph1"/><w:numPr><w:ilvl w:val="0"/><w:numId w:val="1"/></w:numPr><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs><w:spacing w:after="0"/><w:rPr><w:rFonts w:ascii="Calibri" w:hAnsi="Calibri"/><w:sz w:val="24"/><w:szCs w:val="24"/></w:rPr></w:pPr><w:r><w:rPr><w:rFonts w:ascii="Calibri" w:hAnsi="Calibri"/><w:sz w:val="24"/><w:szCs w:val="24"/></w:rPr><w:t xml:space="preserve">Fissures i esquerdes: a parets, façanes i pilars. </w:t></w:r></w:p><w:p w14:paraId="37485C70" w14:textId="77777777" w:rsidR="00385E0F" w:rsidRDefault="00385E0F" w:rsidP="00385E0F"><w:pPr><w:pStyle w:val="ListParagraph1"/><w:numPr><w:ilvl w:val="0"/><w:numId w:val="1"/></w:numPr><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs><w:spacing w:after="0"/></w:pPr><w:r><w:rPr><w:rFonts w:ascii="Calibri" w:hAnsi="Calibri"/><w:sz w:val="24"/><w:szCs w:val="24"/></w:rPr><w:t>Taques d’òxid en elements metàl·lics.</w:t></w:r><w:r><w:t xml:space="preserve"> </w:t></w:r></w:p><w:p w14:paraId="6E0F11F9" w14:textId="77777777" w:rsidR="00385E0F" w:rsidRDefault="00385E0F" w:rsidP="00385E0F"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs></w:pPr></w:p><w:p w14:paraId="53315CC4" w14:textId="77777777" w:rsidR="00385E0F" w:rsidRDefault="00385E0F" w:rsidP="00385E0F"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions de manteniment</w:t></w:r></w:p><w:p w14:paraId="27C0A6D8" w14:textId="77777777" w:rsidR="00385E0F" w:rsidRDefault="00385E0F" w:rsidP="00385E0F"><w:pPr><w:widowControl/><w:tabs><w:tab w:val="num" w:pos="0"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr></w:p><w:p w14:paraId="24CD6591" w14:textId="77777777" w:rsidR="00500DE7" w:rsidRDefault="00500DE7"><w:bookmarkStart w:id="0" w:name="_GoBack"/><w:bookmarkEnd w:id="0"/></w:p>'
  	when 'tancaments'
  		return '<w:p w14:paraId="182CB042" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions d’ús</w:t></w:r></w:p><w:p w14:paraId="0572A2A8" w14:textId="77777777" w:rsidR="008F620F" w:rsidRPr="006D7DEB" w:rsidRDefault="008F620F" w:rsidP="008F620F"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr></w:p><w:p w14:paraId="19D6AFDF" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"><w:r><w:t>Les façanes separen l’habitatge de l’ambient exterior, per aquest motiu, han de complir importants exigències d’aïllament respecte del fred o la calor, el soroll, l’entrada d’aire i d’humitat, la resistència, la seguretat al robatori, etc.</w:t></w:r></w:p><w:p w14:paraId="5C25B143" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"/><w:p w14:paraId="2D0E3A79" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"><w:r><w:t xml:space="preserve">La façana constitueix la imatge externa de la casa i dels seus ocupants, conforma el carrer i, per tant, configura l’aspecte de la nostra ciutat. Per això, no pot alterar-se (tancar balcons amb vidre, obrir noves obertures, instal·lar tendals o rètols no apropiats) sense tenir en compte les ordenances municipals i l’aprovació de la Comunitat de Propietaris. </w:t></w:r></w:p><w:p w14:paraId="15E5F359" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"/><w:p w14:paraId="31B56A6F" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"><w:r><w:t xml:space="preserve">Els acabats de la façana acostumen a ser uns dels punts més fràgils de l’edifici ja que estan en contacte directe amb la intempèrie. D’altra banda, el que inicialment potser només és brutícia o una degradació de la imatge estètica de la façana, es pot convertir en un perill, ja que qualsevol despreniment cauria directament sobre el carrer. </w:t></w:r></w:p><w:p w14:paraId="17C0FAA5" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"/><w:p w14:paraId="615C4902" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"><w:r><w:t xml:space="preserve">Els aplacats de pedra natural s’embruten amb molta facilitat depenent de la porositat de la pedra. Consulteu al vostre Tècnic de Capçalera la possibilitat d’aplicar un producte protector incolor. </w:t></w:r></w:p><w:p w14:paraId="2C69ACFE" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"/><w:p w14:paraId="6D1E48E3" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"><w:r><w:t xml:space="preserve">Les parets mitgeres són aquelles que separen l’edifici dels edificis veïns. Quan no hi hagi edificis veïns o siguin més baixos, les mitgeres quedaran a la vista i hauran d’estar protegides com si fossin façanes. </w:t></w:r></w:p><w:p w14:paraId="3DDF2AA5" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"/><w:p w14:paraId="3573D149" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"><w:r><w:t xml:space="preserve">Als balcons i les galeries no s’han de col·locar càrregues pesades, com ara jardineres o materials emmagatzemats. També s’hauria d’evitar que l’aigua que s’utilitza per regar regalimi per la façana. </w:t></w:r></w:p><w:p w14:paraId="3730B317" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"/><w:p w14:paraId="62533D77" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"><w:r><w:t>Les finestres i balcons exteriors són elements comuns de l’edifici, tot i que el seu ús sigui majoritàriament privat. Qualsevol modificació de la seva imatge exterior (incloent-hi el canvi de perfileria) haurà de ser aprovada per la Comunitat de Propietaris. Tanmateix, la neteja i el manteniment correspon als usuaris dels habitatges.</w:t></w:r></w:p><w:p w14:paraId="2E69DC58" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"/><w:p w14:paraId="2E36D4D7" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"><w:r><w:t>No es recolzaran, sobre les finestres i balcons, elements de subjecció de bastides, politges per aixecar càrregues o mobles, mecanismes de neteja exteriors o d’altres objectes que els puguin malmetre.</w:t></w:r></w:p><w:p w14:paraId="089010C7" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"/><w:p w14:paraId="19E05F83" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"><w:r><w:t>No s’han de donar cops forts a les finestres. D’altra banda, les finestres poden aconseguir una alta estanquitat a l’aire i al soroll amb la col·locació de ribets especialment concebuts per aquesta finalitat.</w:t></w:r></w:p><w:p w14:paraId="4FBCE76D" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"/><w:p w14:paraId="1D071B57" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"><w:r><w:t xml:space="preserve">Els vidres s’hauran de netejar amb aigua sabonosa, preferentment tèbia, i posteriorment s’assecaran. No s’han de fregar amb draps secs, ja que el vidre es ratllaria. </w:t></w:r></w:p><w:p w14:paraId="34B08ACF" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"/><w:p w14:paraId="73C9F57F" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"><w:r><w:t xml:space="preserve">A les persianes enrotllables de PVC, s’ha d’evitar forçar les lamel·les quan perdin l’horitzontalitat o es quedin encallades a les guies. S’ha de netejar amb detergents no alcalins i aigua calenta. Cal utilitzar un drap suau o una esponja. </w:t></w:r></w:p><w:p w14:paraId="25DC0239" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"/><w:p w14:paraId="044E0C91" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"><w:r><w:t>Una manca d’aïllament tèrmic pot ser la causa de l’existència d’humitats de condensació. El Tècnic de Capçalera haurà d’analitzar els símptomes adequadament per tal de determinar possibles defectes a l’aïllament tèrmic.</w:t></w:r></w:p><w:p w14:paraId="4144D192" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"/><w:p w14:paraId="08289F0A" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"><w:r><w:t>Si l’aïllament tèrmic es mulla, perd la seva efectivitat. Per tant, s’ha d’evitar qualsevol tipus d’humitat que el pugui afectar.</w:t></w:r></w:p><w:p w14:paraId="196A03AC" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"/><w:p w14:paraId="131BAA70" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"><w:r><w:t xml:space="preserve">El soroll es transmet per l’aire o per mitjà dels materials de l’edifici. Pot provenir del carrer o de l’interior de la casa. El soroll del carrer es pot reduir mitjançant finestres amb doble vidre o dobles finestres. El soroll de les persones es pot reduir amb la col·locació de materials aïllants o absorbents acústics a parets i sostres. </w:t></w:r></w:p><w:p w14:paraId="27665312" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"/><w:p w14:paraId="1B8D9EDA" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"><w:r><w:t>Per a les reposicions dels elements que tinguin una durada més curta que la pròpia façana (junts, proteccions, etc.) o dels tancaments de vidre, s’utilitzaran productes idèntics als existents o de característiques equivalents que no alterin les seves prestacions inicials.</w:t></w:r></w:p><w:p w14:paraId="2313C559" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"/><w:p w14:paraId="4886AAFE" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"><w:r><w:t xml:space="preserve">Els despreniments d’elements de la façana, són un risc, tant pels usuaris com pels vianants. </w:t></w:r></w:p><w:p w14:paraId="715AACEF" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"/><w:p w14:paraId="3EA944C9" w14:textId="77777777" w:rsidR="008F620F" w:rsidRDefault="008F620F" w:rsidP="008F620F"><w:r><w:t>És responsabilitat de l’usuari, que, quan hi hagi símptomes de degradacions, bufats i/o elements trencats a les façanes, avisar ràpidament als responsables de manteniment de l’edifici perquè es prenguin les mesures oportunes. En cas de perill imminent cal avisar al Servei de Bombers.</w:t></w:r></w:p><w:p></w:p><w:p w14:paraId="5CB52117" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions de manteniment</w:t></w:r></w:p><w:p w14:paraId="2BC35CC1" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr></w:p>'
  	when 'cobertes'
  		return '<w:p w14:paraId="3869DA68" w14:textId="77777777" w:rsidR="002B3380" w:rsidRPr="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions d’ús</w:t></w:r></w:p><w:p w14:paraId="4FEC8DD7" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"/><w:p w14:paraId="410A4493" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"><w:r><w:t>Cobertes planes:</w:t></w:r></w:p><w:p w14:paraId="41F5A294" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"/><w:p w14:paraId="076AE62E" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"><w:r><w:t>Les cobertes planes s’han de mantenir netes i sense herbes, especialment les buneres, les canals i els aiguafons. És preferible no col·locar jardineres a prop dels desguassos o bé que estiguin elevades del sòl per permetre el pas de l’aigua.</w:t></w:r></w:p><w:p w14:paraId="2B0D2118" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"/><w:p w14:paraId="31BB59E8" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"><w:r><w:t>Aquest tipus de coberta només ha d’utilitzar-se per a l’ús a què hagi estat projectada. En aquest sentit, s’evitarà l’emmagatzematge de materials, mobles, etc., i l’abocament de productes químics agressius com ara olis, dissolvents o lleixiu.</w:t></w:r></w:p><w:p w14:paraId="7B74D7FE" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"/><w:p w14:paraId="4C8F2CC0" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"><w:r><w:t>Si a la coberta s’hi instal·len noves antenes, equips d’aire condicionat o, en general, aparells que requereixin ser fixats, la subjecció no ha d’afectar la impermeabilització.</w:t></w:r></w:p><w:p w14:paraId="2BB496CC" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"/><w:p w14:paraId="4A09C9CE" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"><w:r><w:t xml:space="preserve">Tampoc no s’han d’utilitzar com a punts d’ancoratge de tensors, de baranes metàl·liques o d’obra, ni de conductes d’evacuació de fums existents, llevat que el Tècnic de Capçalera ho autoritzi. Si aquestes noves instal·lacions precisen d’un manteniment periòdic, es preveuran al seu entorn les proteccions adequades. </w:t></w:r></w:p><w:p w14:paraId="748FB86D" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"/><w:p w14:paraId="48A33092" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"><w:r><w:t xml:space="preserve">En cas que s’observin humitats als pisos sota coberta, caldrà controlar-les, ja que poden tenir un efecte negatiu sobre els elements estructurals. </w:t></w:r></w:p><w:p w14:paraId="378B3E5C" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"/><w:p w14:paraId="73E429C8" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"><w:r><w:t xml:space="preserve">Cal procurar, sempre que sigui possible, no caminar per sobre de les cobertes planes no transitables. Quan sigui necessari trepitjar-les s’ha d’anar amb molt de compte per no produir desperfectes. El personal d’inspecció, conservació o reparació estarà proveït de sabates de sola tova. </w:t></w:r></w:p><w:p w14:paraId="1323E01C" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"/><w:p w14:paraId="62594EC9" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"/><w:p w14:paraId="17307334" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"><w:r><w:t>Cobertes amb pendent:</w:t></w:r></w:p><w:p w14:paraId="38C040DF" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"/><w:p w14:paraId="42858094" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"><w:r><w:t>Les cobertes s’han de mantenir netes i sense herbes, especialment les buneres, les canals i els aiguafons. S’ha de procurar, sempre que sigui possible, no trepitjar les cobertes en pendent. Quan s’hi transiti cal anar amb molt de compte per no produir desperfectes.</w:t></w:r></w:p><w:p w14:paraId="16A2983E" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"/><w:p w14:paraId="76B73A44" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"><w:r><w:t>Les cobertes en pendent seran accessibles només per a la seva conservació. El personal encarregat del treball anirà proveït de cinturó de seguretat que subjectarà a dos ganxos de servei o a punts fixos de la coberta. És recomanable que els operaris portin sabates amb soles toves i antilliscants. No es transitarà sobre les cobertes si estan mullades.</w:t></w:r></w:p><w:p w14:paraId="6E43484C" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"/><w:p w14:paraId="263986B8" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"><w:r><w:t>Si a la coberta s’instal·len noves antenes, equips d’aire condicionat o, en general, aparells que requereixin ser fixats, la subjecció no pot afectar la impermeabilització. Tampoc no s’han d’utilitzar com a punts d’ancoratge de tensors, de baranes metàl·liques o d’obra, ni de conductes d’evacuació de fums existents, llevat que un tècnic especialitzat ho autoritzi. Si aquestes noves instal·lacions necessiten d’un manteniment periòdic, caldrà preveure al seu entorn les proteccions adequades.</w:t></w:r></w:p><w:p w14:paraId="07F2051B" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"/><w:p w14:paraId="6ABAA27D" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"><w:r><w:lastRenderedPageBreak/><w:t>En el cas que s’observin humitats a les plantes sota coberta, s’hauran de controlar, ja que poden tenir un efecte negatiu sobre els elements estructurals.</w:t></w:r></w:p><w:p w14:paraId="14F892C5" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"/><w:p w14:paraId="1F233EA0" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"><w:r><w:t>La molsa i els fongs s’eliminaran amb un raspall i si cal s’aplicarà un fungicida.</w:t></w:r></w:p><w:p w14:paraId="2626364D" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"><w:r><w:t xml:space="preserve">Els treballs de reparació es faran sempre retirant la part malmesa per tal de no sobrecarregar l’estructura. </w:t></w:r></w:p><w:p w14:paraId="22FDE7F7" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"/><w:p w14:paraId="01B8A6DB" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"><w:bookmarkStart w:id="0" w:name="_GoBack"/><w:bookmarkEnd w:id="0"/></w:p><w:p w14:paraId="23E5D4E9" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"><w:r><w:t>Lluernes i claraboies:</w:t></w:r></w:p><w:p w14:paraId="4325AEB6" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"/><w:p w14:paraId="17AC5236" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"><w:r><w:t>Les claraboies i lluernes s’han de netejar amb assiduïtat, ja que en cas d’embrutar-se redueixen considerablement la quantitat de llum que deixen passar.</w:t></w:r></w:p><w:p w14:paraId="48DAB024" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"/><w:p w14:paraId="08A43CB5" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"><w:r><w:t xml:space="preserve">Per la seva situació dins l’edifici, han d’extremar-se les mesures de seguretat en el moment de netejar-les per tal d’evitar accidents. </w:t></w:r></w:p><w:p w14:paraId="1FB9A740" w14:textId="77777777" w:rsidR="00500DE7" w:rsidRDefault="00500DE7"/><w:p></w:p><w:p w14:paraId="5CB52117" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions de manteniment</w:t></w:r></w:p><w:p w14:paraId="2BC35CC1" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr></w:p>'
  	when 'particions'
  		return '<w:p w14:paraId="3869DA68" w14:textId="77777777" w:rsidR="002B3380" w:rsidRPr="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions d’ús</w:t></w:r></w:p><w:p w14:paraId="4FEC8DD7" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"/><w:p></w:p><w:p w14:paraId="5CB52117" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions de manteniment</w:t></w:r></w:p><w:p w14:paraId="2BC35CC1" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr></w:p>'
  	when 'sanejament'
  		return '<w:p w14:paraId="56032BEB" w14:textId="77777777" w:rsidR="005F0624" w:rsidRDefault="005F0624" w:rsidP="005F0624"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions d’ús</w:t></w:r></w:p><w:p w14:paraId="52B8C796" w14:textId="77777777" w:rsidR="005F0624" w:rsidRDefault="005F0624" w:rsidP="005F0624"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr></w:p><w:p w14:paraId="15CDB0F1" w14:textId="77777777" w:rsidR="005F0624" w:rsidRDefault="005F0624" w:rsidP="005F0624"><w:r><w:t>La xarxa de sanejament es compon bàsicament d’elements i conductes de desguàs dels aparells de cada planta i d’alguns recintes de l’edifici, que connecten amb la xarxa de sanejament vertical (baixants) i amb els claveguerons, pericons, col·lectors, etc., fins a la xarxa municipal o un altre sistema autoritzat.</w:t></w:r></w:p><w:p w14:paraId="0F09D32A" w14:textId="77777777" w:rsidR="005F0624" w:rsidRDefault="005F0624" w:rsidP="005F0624"/><w:p w14:paraId="06F69BEC" w14:textId="77777777" w:rsidR="005F0624" w:rsidRDefault="005F0624" w:rsidP="005F0624"><w:r><w:t>A la xarxa de sanejament és molt important conservar la instal·lació neta i lliure de dipòsits. Es pot aconseguir amb un manteniment reduït basat en una utilització adequada i en uns hàbits higiènics correctes per part dels usuaris.</w:t></w:r></w:p><w:p w14:paraId="3DC85FE9" w14:textId="77777777" w:rsidR="005F0624" w:rsidRDefault="005F0624" w:rsidP="005F0624"><w:r><w:t>La xarxa d’evacuació d’aigua, sobretot l’inodor, no es pot utilitzar com a abocador de deixalles. No s’hi poden llençar plàstics, cotó, gomes, compreses, fulles d’afaitar, bastonets, etc. Les substàncies i els elements anteriors, per si mateixos o combinats, poden taponar o, fins i tot, destruir per procediments físics o reaccions químiques les conduccions i/o els seus elements, produint-se vessaments pudents com ara fuites, taques, etc.</w:t></w:r></w:p><w:p w14:paraId="4B1449CE" w14:textId="77777777" w:rsidR="005F0624" w:rsidRDefault="005F0624" w:rsidP="005F0624"/><w:p w14:paraId="64604DB5" w14:textId="77777777" w:rsidR="005F0624" w:rsidRDefault="005F0624" w:rsidP="005F0624"><w:r><w:t>S’han de revisar amb freqüència els sifons de les buneres i comprovar que no els falti aigua, per tal d’evitar que les olors de la xarxa surtin a l’exterior.</w:t></w:r></w:p><w:p w14:paraId="5D920BD8" w14:textId="77777777" w:rsidR="005F0624" w:rsidRDefault="005F0624" w:rsidP="005F0624"/><w:p w14:paraId="46DD374F" w14:textId="77777777" w:rsidR="005F0624" w:rsidRDefault="005F0624" w:rsidP="005F0624"><w:r><w:t>Per desembussar els conductes no es poden utilitzar àcids o productes que perjudiquin els desguassos. S’utilitzaran sempre detergents biodegradables per tal d’evitar la creació d’escumes que petrifiquin dins dels sifons i dels pericons de l’edifici. Tampoc s’abocaran aigües que continguin olis, colorants permanents o substàncies tòxiques. Com a exemple, un litre d’oli mineral contamina 10.000 litres d’aigua.</w:t></w:r></w:p><w:p w14:paraId="2F9FA35F" w14:textId="77777777" w:rsidR="005F0624" w:rsidRDefault="005F0624" w:rsidP="005F0624"/><w:p w14:paraId="1A4B4CE7" w14:textId="77777777" w:rsidR="005F0624" w:rsidRDefault="005F0624" w:rsidP="005F0624"><w:r><w:t>Qualsevol modificació a la instal·lació o a les condicions d’ús que puguin alterar el normal funcionament serà feta mitjançant un estudi previ i sota la direcció del vostre Tècnic de Capçalera.</w:t></w:r></w:p><w:p w14:paraId="55D20824" w14:textId="77777777" w:rsidR="005F0624" w:rsidRDefault="005F0624" w:rsidP="005F0624"/><w:p w14:paraId="758F3F31" w14:textId="77777777" w:rsidR="00500DE7" w:rsidRDefault="005F0624"><w:r><w:t xml:space="preserve">Les possibles fuites es localitzaran i repararan al més aviat possible. </w:t></w:r><w:bookmarkStart w:id="0" w:name="_GoBack"/><w:bookmarkEnd w:id="0"/></w:p><w:p></w:p><w:p w14:paraId="5CB52117" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions de manteniment</w:t></w:r></w:p><w:p w14:paraId="2BC35CC1" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr></w:p>'
  	
  	when 'aigua'
  		return '<w:p w14:paraId="29B495E9" w14:textId="77777777" w:rsidR="00220DEF" w:rsidRDefault="00220DEF" w:rsidP="00220DEF"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions d’ús</w:t></w:r></w:p><w:p w14:paraId="0FE7C5A4" w14:textId="77777777" w:rsidR="00220DEF" w:rsidRPr="006D7DEB" w:rsidRDefault="00220DEF" w:rsidP="00220DEF"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr></w:p><w:p w14:paraId="4995EEAF" w14:textId="77777777" w:rsidR="00220DEF" w:rsidRDefault="00220DEF" w:rsidP="00220DEF"><w:r><w:t>Responsabilitats:</w:t></w:r></w:p><w:p w14:paraId="17F27BB4" w14:textId="77777777" w:rsidR="00220DEF" w:rsidRDefault="00220DEF" w:rsidP="00220DEF"><w:r><w:t>El manteniment de la instal·lació a partir del comptador (no només des de la clau de pas de l’habitatge) és a càrrec de cadascun dels usuaris. El manteniment de les instal·lacions situades entre la clau de pas de l’edifici i els comptadors correspon al propietari de l’immoble o a la Comunitat de Propietaris.</w:t></w:r></w:p><w:p w14:paraId="4C8070D9" w14:textId="77777777" w:rsidR="00220DEF" w:rsidRDefault="00220DEF" w:rsidP="00220DEF"/><w:p w14:paraId="7676011B" w14:textId="77777777" w:rsidR="00220DEF" w:rsidRDefault="00220DEF" w:rsidP="00220DEF"><w:r><w:t>La cambra de comptadors serà accessible només per al porter o vigilant i el personal de la companyia subministradora o de manteniment. Cal vigilar que les reixes de ventilació no estiguin obstruïdes, així com també l’accés a la cambra.</w:t></w:r></w:p><w:p w14:paraId="28156591" w14:textId="77777777" w:rsidR="00220DEF" w:rsidRDefault="00220DEF" w:rsidP="00220DEF"/><w:p w14:paraId="57E62FC9" w14:textId="77777777" w:rsidR="00220DEF" w:rsidRDefault="00220DEF" w:rsidP="00220DEF"><w:r><w:t>Precaucions:</w:t></w:r></w:p><w:p w14:paraId="34C41021" w14:textId="77777777" w:rsidR="00220DEF" w:rsidRDefault="00220DEF" w:rsidP="00220DEF"><w:r><w:t>Es recomana tancar la clau de pas de l’habitatge en cas d’absència prolongada. Si l’absència ha estat molt llarga s’han de revisar els junts abans d’obrir la clau de pas.</w:t></w:r></w:p><w:p w14:paraId="60ABA138" w14:textId="77777777" w:rsidR="00220DEF" w:rsidRDefault="00220DEF" w:rsidP="00220DEF"><w:r><w:t>Totes les fuites o defectes de funcionament a les conduccions, els accessoris o els equips es repararan immediatament.</w:t></w:r></w:p><w:p w14:paraId="3354DC61" w14:textId="77777777" w:rsidR="00220DEF" w:rsidRDefault="00220DEF" w:rsidP="00220DEF"/><w:p w14:paraId="59973274" w14:textId="77777777" w:rsidR="00220DEF" w:rsidRDefault="00220DEF" w:rsidP="00220DEF"><w:r><w:t>Totes les canalitzacions metàl·liques es connectaran a la xarxa de posada a terra. És prohibit d’utilitzar les canonades com a elements de contacte de les instal·lacions elèctriques amb el terra.</w:t></w:r></w:p><w:p w14:paraId="18777558" w14:textId="77777777" w:rsidR="00220DEF" w:rsidRDefault="00220DEF" w:rsidP="00220DEF"/><w:p w14:paraId="39DD6558" w14:textId="77777777" w:rsidR="00220DEF" w:rsidRDefault="00220DEF" w:rsidP="00220DEF"><w:r><w:t>Per desembussar canonades, no s’han d’utilitzar objectes punyents que puguin perforar-les.</w:t></w:r></w:p><w:p w14:paraId="7D85B988" w14:textId="77777777" w:rsidR="00220DEF" w:rsidRDefault="00220DEF" w:rsidP="00220DEF"><w:r><w:t>En cas de baixes temperatures, s’ha de deixar córrer l’aigua per les canonades per tal d’evitar que es geli l’aigua al seu interior.</w:t></w:r></w:p><w:p w14:paraId="0E2B8B13" w14:textId="77777777" w:rsidR="00220DEF" w:rsidRDefault="00220DEF" w:rsidP="00220DEF"/><w:p w14:paraId="37830456" w14:textId="77777777" w:rsidR="00220DEF" w:rsidRDefault="00220DEF" w:rsidP="00220DEF"><w:r><w:t>El correcte funcionament de la xarxa d’aigua calenta és un dels factors que influeixen més decididament en l’estalvi d’energia, per aquest motiu ha de ser objecte d’una atenció més gran per obtenir un rendiment energètic òptim.</w:t></w:r></w:p><w:p w14:paraId="384A2611" w14:textId="77777777" w:rsidR="00220DEF" w:rsidRDefault="00220DEF" w:rsidP="00220DEF"/><w:p w14:paraId="0FA33751" w14:textId="77777777" w:rsidR="00220DEF" w:rsidRDefault="00220DEF" w:rsidP="00220DEF"><w:r><w:t>A la revisió general s’ha de comprovar l’estat de l’aïllament i la senyalització de la xarxa d’aigua, l’estanquitat de les unions i els junts, i el correcte funcionament de les claus de pas i vàlvules, i cal verificar la possibilitat de tancament total o parcial de la xarxa.</w:t></w:r></w:p><w:p w14:paraId="038DC970" w14:textId="77777777" w:rsidR="00500DE7" w:rsidRDefault="00500DE7"><w:bookmarkStart w:id="0" w:name="_GoBack"/><w:bookmarkEnd w:id="0"/></w:p><w:p></w:p><w:p w14:paraId="5CB52117" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions de manteniment</w:t></w:r></w:p><w:p w14:paraId="2BC35CC1" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr></w:p>'
  	when 'electricitat'
  		return '<w:p w14:paraId="3AFB7001" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions d’ús</w:t></w:r></w:p><w:p w14:paraId="5B049B85" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRPr="006D7DEB" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr></w:p><w:p w14:paraId="5ACF3041" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"><w:r><w:t>La instal·lació elèctrica està formada pel comptador, per la derivació individual, pel quadre general de comandament i protecció i pels circuits de distribució interior. Al mateix temps, el quadre general de comandament i protecció està format per un interruptor de control de potència (ICP), un interruptor diferencial (ID) i els petits interruptors automàtics (PIA).</w:t></w:r></w:p><w:p w14:paraId="597B1FE1" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"/><w:p w14:paraId="6382D4A6" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"><w:r><w:t>L’ICP és el mecanisme que controla la potència que subministra la xarxa de companyia. L’ICP desconnecta la instal·lació quan la potència consumida és superior a la contractada o bé quan es produeix un curtcircuit (contacte directe entre dos fils conductors) i el PIA del seu circuit no es dispara prèviament.</w:t></w:r></w:p><w:p w14:paraId="2D24E34F" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"/><w:p w14:paraId="7B3122F7" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"><w:r><w:t>L’interruptor diferencial (ID) protegeix contra les fuites accidentals de corrent com, per exemple, les que es produeixen quan es toca amb el dit un endoll o quan un fil elèctric toca una canonada d’aigua o la carcassa de la rentadora. L’interruptor diferencial (ID) és indispensable per tal d’evitar accidents. Sempre que es produeix una fuita salta l’interruptor.</w:t></w:r></w:p><w:p w14:paraId="271752B2" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"/><w:p w14:paraId="44CC3EF8" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"><w:r><w:t>Cada circuit de distribució interior té assignat un PIA que salta quan el consum del circuit és superior al previst. Aquest interruptor protegeix contra els curtcircuits i les sobrecàrregues.</w:t></w:r></w:p><w:p w14:paraId="6A9894CC" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"/><w:p w14:paraId="7A9134BE" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"><w:r><w:t>Responsabilitats:</w:t></w:r></w:p><w:p w14:paraId="2FA146AC" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"><w:r><w:t>El manteniment de la instal·lació elèctrica a partir del comptador (i no només des del quadre general d’entrada a l’habitatge) és a càrrec de cadascun dels usuaris.</w:t></w:r></w:p><w:p w14:paraId="592112D1" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"/><w:p w14:paraId="64DBD010" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"><w:r><w:t>El manteniment de la instal·lació entre la caixa general de protecció i els comptadors correspon al propietari de l’immoble o a la Comunitat de Propietaris. Tot i que la instal·lació elèctrica té desgasts molt petits, difícils d’apreciar, és convenient fer revisions periòdiques per tal de comprovar el bon funcionament dels mecanismes i l’estat del cablejat, de les connexions i de l’aïllament. A la revisió general de la instal·lació elèctrica s’ha de verificar la canalització de les derivacions individuals comprovant l’estat dels conductes, fixacions, aïllament i tapes de registre, i verificar l’absència d’humitat.</w:t></w:r></w:p><w:p w14:paraId="5FC9E47B" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"/><w:p w14:paraId="5F4F2763" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"><w:r><w:t>La cambra de comptadors serà accessible només per al porter o vigilant, i el personal de la companyia subministradora o de manteniment. S’ha de vigilar que les reixes de ventilació no estiguin obstruïdes, així com també l’accés a la cambra.</w:t></w:r></w:p><w:p w14:paraId="7AA6DF99" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"/><w:p w14:paraId="6EDCDA3A" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"><w:r><w:t>Precaucions</w:t></w:r></w:p><w:p w14:paraId="29ACE7C5" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"><w:r><w:t>Les instal·lacions elèctriques s’han d’utilitzar amb precaució pel perill que comporten. Està prohibit manipular els circuits i els quadres generals, aquestes operacions han de ser efectuades exclusivament per personal especialista.</w:t></w:r></w:p><w:p w14:paraId="69443E6A" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"/><w:p w14:paraId="7A1B5E24" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"><w:r><w:t>No s’ha de permetre als nens manipular els aparells elèctrics quan estiguin endollats i, en general, s’ha d’evitar manipular-los amb les mans humides. S’ha de tenir especial cura en les instal·lacions de banys i cuines (locals humits).</w:t></w:r></w:p><w:p w14:paraId="06F79207" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"/><w:p w14:paraId="62A9CF43" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"><w:r><w:lastRenderedPageBreak/><w:t>No es poden connectar als endolls aparells de potència superior a la prevista o diversos aparells que, en conjunt, tinguin una potència superior. Si s’aprecia un escalfament dels cables o dels endolls connectats a un determinat punt, s’han de desconnectar. És símptoma que la instal·lació està sobrecarregada o no està preparada per tal de rebre l’aparell. Les clavilles dels endolls han d’estar ben cargolades per tal d’evitar que facin espurnes. Les connexions dolentes originen escalfaments que poden generar un incendi.</w:t></w:r></w:p><w:p w14:paraId="27812A70" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"/><w:p w14:paraId="2145D026" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"><w:r><w:t>És recomanable tancar l’interruptor de control de potència (ICP) de l’habitatge en cas d’absència prolongada. Si es deixa el frigorífic en funcionament, no és possible desconnectar l’interruptor de control de potència, però sí tancar els petits interruptors automàtics d’altres circuits.</w:t></w:r></w:p><w:p w14:paraId="0E4F03E5" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"/><w:p w14:paraId="0855239D" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"><w:r><w:t>Periòdicament, és recomanable prémer el botó de prova del diferencial (ID), el qual ha de desconnectar tota la instal·lació. Si no la desconnecta, el quadre no ofereix protecció i caldrà avisar l’instal·lador.</w:t></w:r></w:p><w:p w14:paraId="4543F316" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"/><w:p w14:paraId="5AD15D8B" w14:textId="77777777" w:rsidR="000F2F85" w:rsidRPr="002F419F" w:rsidRDefault="000F2F85" w:rsidP="000F2F85"><w:r><w:t>Per netejar els llums i les plaques dels mecanismes elèctrics s’ha de desconnectar la instal·lació elèctrica. S’han de netejar amb un drap lleugerament humit amb aigua i detergent. L’electricitat es connectarà un cop s’hagin assecat les plaques.</w:t></w:r></w:p><w:p w14:paraId="2CFE936F" w14:textId="77777777" w:rsidR="00500DE7" w:rsidRDefault="00500DE7"><w:bookmarkStart w:id="0" w:name="_GoBack"/><w:bookmarkEnd w:id="0"/></w:p><w:p></w:p><w:p w14:paraId="5CB52117" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions de manteniment</w:t></w:r></w:p><w:p w14:paraId="2BC35CC1" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr></w:p>'
  	when 'climatitzacio'
  		return '<w:p w14:paraId="124F39A7" w14:textId="77777777" w:rsidR="00971A1D" w:rsidRDefault="00971A1D" w:rsidP="00971A1D"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions d’ús</w:t></w:r></w:p><w:p w14:paraId="046364CF" w14:textId="77777777" w:rsidR="00971A1D" w:rsidRPr="006D7DEB" w:rsidRDefault="00971A1D" w:rsidP="00971A1D"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr></w:p><w:p w14:paraId="741F01F5" w14:textId="77777777" w:rsidR="00971A1D" w:rsidRDefault="00971A1D" w:rsidP="00971A1D"><w:r><w:t>S’han de llegir i seguir les instruccions de la instal·lació abans de posar-la en funcionament per primera vegada.</w:t></w:r></w:p><w:p w14:paraId="649B0D03" w14:textId="77777777" w:rsidR="00971A1D" w:rsidRDefault="00971A1D" w:rsidP="00971A1D"/><w:p w14:paraId="2B4F136B" w14:textId="77777777" w:rsidR="00971A1D" w:rsidRDefault="00971A1D" w:rsidP="00971A1D"><w:r><w:t xml:space="preserve">El correcte funcionament de la instal·lació és un dels factors que influeixen més decisivament en l’estalvi d’energia, per tant s’ha de mantenir amb cura per obtenir un rendiment òptim. </w:t></w:r></w:p><w:p w14:paraId="13A8B5DB" w14:textId="77777777" w:rsidR="00971A1D" w:rsidRDefault="00971A1D" w:rsidP="00971A1D"/><w:p w14:paraId="4A555F0F" w14:textId="77777777" w:rsidR="00971A1D" w:rsidRDefault="00971A1D" w:rsidP="00971A1D"><w:r><w:t>Cal informar al personal de manteniment si es detecten fuites d’aigua en els aparells emissors o en les conduccions i es procedirà a la seva reparació immediata. Si és el cas s’avisarà del fet a la Propietat.</w:t></w:r></w:p><w:p w14:paraId="482CADBF" w14:textId="77777777" w:rsidR="00971A1D" w:rsidRDefault="00971A1D" w:rsidP="00971A1D"/><w:p w14:paraId="7512566B" w14:textId="77777777" w:rsidR="00971A1D" w:rsidRDefault="00971A1D" w:rsidP="00971A1D"><w:r><w:t>Els aparells emissors de calor no han de tapar-se amb cap moble o objecte.</w:t></w:r></w:p><w:p w14:paraId="5B8A4580" w14:textId="77777777" w:rsidR="00971A1D" w:rsidRPr="00B04FDC" w:rsidRDefault="00971A1D" w:rsidP="00971A1D"><w:r><w:t>Després d’una fuita de gas, no es pot encendre la calefacció fins que l’instal·lador l’hagi revisada.</w:t></w:r></w:p><w:p w14:paraId="62991870" w14:textId="77777777" w:rsidR="00500DE7" w:rsidRDefault="00500DE7"><w:bookmarkStart w:id="0" w:name="_GoBack"/><w:bookmarkEnd w:id="0"/></w:p><w:p></w:p><w:p w14:paraId="5CB52117" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions de manteniment</w:t></w:r></w:p><w:p w14:paraId="2BC35CC1" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr></w:p>'
  	when 'gas'
  		return '<w:p w14:paraId="6CE648ED" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions d’ús</w:t></w:r></w:p><w:p w14:paraId="5988978B" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRPr="006D7DEB" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr></w:p><w:p w14:paraId="682A9B9E" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"><w:r><w:t>El grau de perillositat de les instal·lacions de gas combustible és superior a qualsevol altra. Per aquesta raó s’extremaran les mesures de seguretat. Qualsevol intervenció sobre la instal·lació o sobre els aparells de consum ha d’estar feta per una empresa especialitzada.</w:t></w:r></w:p><w:p w14:paraId="45A0DA5C" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"/><w:p w14:paraId="46E5CCB8" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"><w:r><w:t>Cal seguir les instruccions de manteniment dels aparells de gas que proporcionen els seus fabricants.</w:t></w:r></w:p><w:p w14:paraId="78EB8BC0" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"/><w:p w14:paraId="7F5D4DF1" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"><w:r><w:t>No es poden obstruir les xemeneies d’evacuació de fums, ni es poden deixar objectes a prop de focus de calor o superfícies calentes.</w:t></w:r></w:p><w:p w14:paraId="398406DE" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"/><w:p w14:paraId="19085748" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"><w:r><w:t>No es poden tapar els forats de ventilació dels espais que contenen instal·lacions de gas. Hi ha d’haver ventilació tant a la part alta com a la part baixa de l’espai.</w:t></w:r></w:p><w:p w14:paraId="70091675" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"><w:r><w:t>Els gasos propà i butà són més pesants que l’aire i, per tant, en cas de fuita van cap avall. El gas natural és menys pesant que l’aire i, per tant, en cas de fuita va cap a amunt.</w:t></w:r></w:p><w:p w14:paraId="27AF7447" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"/><w:p w14:paraId="110C7F51" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"><w:r><w:t>Si es vol donar subministrament a uns altres aparells que els de construcció original s’ha de demanar permís a la Propietat. La instal·lació del nou aparell l’ha de fer una empresa autoritzada.</w:t></w:r></w:p><w:p w14:paraId="277D10C2" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"/><w:p w14:paraId="03F89D26" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"><w:r><w:t>Els cremadors s’han de mantenir nets de dipòsits i residus de la combustió. Es comprovaran periòdicament les emissions de gasos contaminants i si és necessari es faran els ajustos i les reparacions en els aparells per tal de que aquestes es mantinguin dintre dels paràmetres establerts.</w:t></w:r></w:p><w:p w14:paraId="73B176E5" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"/><w:p w14:paraId="7A2B22F4" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"><w:r><w:t>Es recomana que en absències llargues es tanqui l’aixeta de pas general de la instal·lació de gas. Durant la nit és millor fer el mateix, sempre que no quedi en funcionament cap aparell de gas.</w:t></w:r></w:p><w:p w14:paraId="63E8DCE8" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"/><w:p w14:paraId="776F8A44" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"><w:r><w:t>El manteniment de les instal·lacions situades entre l’aixeta d’entrada de l’immoble i el comptador correspon a la Propietat.</w:t></w:r></w:p><w:p w14:paraId="0D1858AE" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"/><w:p w14:paraId="7A0EB5FE" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"><w:r><w:t>Els tubs de gas no s’han de fer servir per a les preses de terra dels aparells elèctrics ni tampoc per a penjar-hi objectes.</w:t></w:r></w:p><w:p w14:paraId="2168C69D" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"/><w:p w14:paraId="4B9839E5" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"><w:r><w:t>La cambra de comptadors només serà accessible al porter, al vigilant, o al personal de la companyia subministradora i al de manteniment. S’ha de vigilar que tant les reixes de ventilació com l’accés a la cambra no quedin obstruïts.</w:t></w:r></w:p><w:p w14:paraId="29F1DD42" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"/><w:p w14:paraId="3EC2095D" w14:textId="77777777" w:rsidR="008E0EAE" w:rsidRDefault="008E0EAE" w:rsidP="008E0EAE"><w:r><w:t>Si es detecta una fuita de gas no s’ha d’obrir cap llum ni accionar interruptors o connectar aparells elèctrics. La connexió o desconnexió d’aparells elèctrics pot provocar guspires que a la seva vegada poden provocar la deflagració del gas acumulat. Tampoc es poden fer servir encenedors ni encendre llumins. En aquests casos s’intentarà tancar la clau de pas del gas i a continuació es ventilarà el recinte a on s’ha produït la fuga obrint portes i finestres de bat a bat. Finalment s’ha d’avisar de l’avaria a una empresa instal·ladora autoritzada o bé al servei d’urgències de l‘empresa subministradora perquè localitzi l’avaria.</w:t></w:r></w:p><w:p w14:paraId="34EE0D34" w14:textId="77777777" w:rsidR="00500DE7" w:rsidRDefault="00500DE7"><w:bookmarkStart w:id="0" w:name="_GoBack"/><w:bookmarkEnd w:id="0"/></w:p><w:p></w:p><w:p w14:paraId="5CB52117" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions de manteniment</w:t></w:r></w:p><w:p w14:paraId="2BC35CC1" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr></w:p>'
  	when 'ventilacio'
  		return '<w:p w14:paraId="7DC3818F" w14:textId="77777777" w:rsidR="000C6FCE" w:rsidRDefault="000C6FCE" w:rsidP="000C6FCE"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions d’ús</w:t></w:r></w:p><w:p w14:paraId="504E5617" w14:textId="77777777" w:rsidR="000C6FCE" w:rsidRPr="006D7DEB" w:rsidRDefault="000C6FCE" w:rsidP="000C6FCE"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr></w:p><w:p w14:paraId="68499608" w14:textId="77777777" w:rsidR="000C6FCE" w:rsidRDefault="000C6FCE" w:rsidP="000C6FCE"><w:r><w:t>S’han de ventilar els espais interiors per evitar humitats de condensació, especialment si la calefacció és d’estufes de gas butà. La ventilació s’ha de fer preferentment en hores de sol, i per espai de 20 o 30 minuts.</w:t></w:r></w:p><w:p w14:paraId="6A0D9D66" w14:textId="77777777" w:rsidR="000C6FCE" w:rsidRDefault="000C6FCE" w:rsidP="000C6FCE"/><w:p w14:paraId="275C52DC" w14:textId="77777777" w:rsidR="000C6FCE" w:rsidRDefault="000C6FCE" w:rsidP="000C6FCE"><w:r><w:t>Els fongs que apareixen en els punts més freds dels recintes tenen el seu origen en humitats de condensació i no en degoters, com es creu normalment. La neteja s’ha de realitzar amb productes fungicides, però s’ha de tenir en compte que de no resoldre el problema d’origen, els fongs tornaran a aparèixer si no es millora la ventilació o l’aïllament tèrmic de la peça.</w:t></w:r></w:p><w:p w14:paraId="1BE37E9E" w14:textId="77777777" w:rsidR="000C6FCE" w:rsidRDefault="000C6FCE" w:rsidP="000C6FCE"/><w:p w14:paraId="017D2BD3" w14:textId="77777777" w:rsidR="000C6FCE" w:rsidRDefault="000C6FCE" w:rsidP="000C6FCE"><w:r><w:t>En cas d’augmentar l’aïllament tèrmic de forma puntual sense reduir la humitat ambiental, la condensació es produirà en un altre punt fred provocant deterioraments dels revestiments.</w:t></w:r></w:p><w:p w14:paraId="3A06E883" w14:textId="77777777" w:rsidR="000C6FCE" w:rsidRDefault="000C6FCE" w:rsidP="000C6FCE"/><w:p w14:paraId="3612BF89" w14:textId="77777777" w:rsidR="000C6FCE" w:rsidRDefault="000C6FCE" w:rsidP="000C6FCE"><w:r><w:t>No és permès de connectar extractors a conductes d’evacuació de gasos de calderes de calefacció.</w:t></w:r></w:p><w:p w14:paraId="084AB6D3" w14:textId="77777777" w:rsidR="000C6FCE" w:rsidRDefault="000C6FCE" w:rsidP="000C6FCE"/><w:p w14:paraId="35547E24" w14:textId="77777777" w:rsidR="000C6FCE" w:rsidRDefault="000C6FCE" w:rsidP="000C6FCE"><w:r><w:t>Cal llegir i seguir les instruccions dels aparells d’extracció mecànica.</w:t></w:r></w:p><w:p w14:paraId="0577FB7B" w14:textId="77777777" w:rsidR="00500DE7" w:rsidRDefault="00500DE7"><w:bookmarkStart w:id="0" w:name="_GoBack"/><w:bookmarkEnd w:id="0"/></w:p><w:p></w:p><w:p w14:paraId="5CB52117" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions de manteniment</w:t></w:r></w:p><w:p w14:paraId="2BC35CC1" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr></w:p>'
  	when 'incendis'
  		return '<w:p w14:paraId="72CF228C" w14:textId="77777777" w:rsidR="00A82C80" w:rsidRDefault="00A82C80" w:rsidP="00A82C80"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions d’ús</w:t></w:r></w:p><w:p w14:paraId="0F6D3F90" w14:textId="77777777" w:rsidR="00A82C80" w:rsidRPr="006D7DEB" w:rsidRDefault="00A82C80" w:rsidP="00A82C80"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr></w:p><w:p w14:paraId="6D2C1D99" w14:textId="77777777" w:rsidR="00A82C80" w:rsidRDefault="00A82C80" w:rsidP="00A82C80"><w:r><w:t>Les instruccions d’ús dels components de la instal·lació figuren en els mateixos aparells. Tots els components de la instal·lació han d’estar al seu lloc i a l’abast. No s’han d’ocultar.</w:t></w:r></w:p><w:p w14:paraId="50786FD1" w14:textId="77777777" w:rsidR="00A82C80" w:rsidRDefault="00A82C80" w:rsidP="00A82C80"/><w:p w14:paraId="37667ADF" w14:textId="77777777" w:rsidR="00A82C80" w:rsidRDefault="00A82C80" w:rsidP="00A82C80"><w:r><w:t>Queda completament prohibit fer un ús indegut dels elements de la instal·lació contra incendis.</w:t></w:r></w:p><w:p w14:paraId="71847D99" w14:textId="77777777" w:rsidR="00A82C80" w:rsidRDefault="00A82C80" w:rsidP="00A82C80"/><w:p w14:paraId="02D34674" w14:textId="77777777" w:rsidR="00A82C80" w:rsidRDefault="00A82C80" w:rsidP="00A82C80"><w:r><w:t>S’ha de mantenir la instal·lació de protecció al foc en condicions reglamentàries. Totes les tasques de manteniment, revisió i reparació han d’estar fetes per una empresa autoritzada.</w:t></w:r></w:p><w:p w14:paraId="7E428AF5" w14:textId="77777777" w:rsidR="00A82C80" w:rsidRDefault="00A82C80" w:rsidP="00A82C80"/><w:p w14:paraId="59D9C966" w14:textId="77777777" w:rsidR="00A82C80" w:rsidRPr="00F55233" w:rsidRDefault="00A82C80" w:rsidP="00A82C80"><w:pPr><w:rPr><w:bCs/></w:rPr></w:pPr><w:r w:rsidRPr="00F55233"><w:rPr><w:bCs/></w:rPr><w:t>En cas d’incendi</w:t></w:r></w:p><w:p w14:paraId="13102A4B" w14:textId="77777777" w:rsidR="00A82C80" w:rsidRDefault="00A82C80" w:rsidP="00A82C80"><w:r><w:t>Si es produeix un conat d’incendi, cal avisar els bombers i als ocupants de l’immoble immediatament. Durant els primers minuts d’inici del foc, és possible intentar controlar-lo amb els mitjans de què disposi l’edifici (mànegues, extintors). En aquest cas sempre han d’actuar dues persones amb un extintor cadascuna o amb una mànega atacant al foc. Mai s’ha d’actuar sol. Si passats uns instants no s’ha dominat el foc, s’aconsella evacuar l’edifici seguint el pla previst d’evacuació de l’immoble.</w:t></w:r></w:p><w:p w14:paraId="6E3349B9" w14:textId="77777777" w:rsidR="00A82C80" w:rsidRDefault="00A82C80" w:rsidP="00A82C80"/><w:p w14:paraId="40A02D2D" w14:textId="77777777" w:rsidR="00A82C80" w:rsidRDefault="00A82C80" w:rsidP="00A82C80"><w:r><w:t>Si en algun conat d’incendi s’ha produït una descàrrega parcial o total d’un extintor, s’han desenrotllat mànegues o s’han accionat polsadors de disparadors d’alarma, cal fer-ho saber al conserge o al personal de manteniment de l’edifici perquè duguin a terme les revisions escaients i restableixin l’estat normal de la instal·lació.</w:t></w:r></w:p><w:p w14:paraId="5BF1F1A3" w14:textId="77777777" w:rsidR="00500DE7" w:rsidRDefault="00500DE7"><w:bookmarkStart w:id="0" w:name="_GoBack"/><w:bookmarkEnd w:id="0"/></w:p><w:p></w:p><w:p w14:paraId="5CB52117" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions de manteniment</w:t></w:r></w:p><w:p w14:paraId="2BC35CC1" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr></w:p>'
  	when 'ascensor'
  		return '<w:p w14:paraId="42D61FFF" w14:textId="77777777" w:rsidR="00AF1C67" w:rsidRDefault="00AF1C67" w:rsidP="00AF1C67"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions d’ús</w:t></w:r></w:p><w:p w14:paraId="2B964564" w14:textId="77777777" w:rsidR="00AF1C67" w:rsidRPr="006D7DEB" w:rsidRDefault="00AF1C67" w:rsidP="00AF1C67"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr></w:p><w:p w14:paraId="3EF0D94E" w14:textId="77777777" w:rsidR="00AF1C67" w:rsidRDefault="00AF1C67" w:rsidP="00AF1C67"><w:r><w:t>Els ascensors no poden ser utilitzats per nens que no vagin acompanyats de persones adultes.</w:t></w:r></w:p><w:p w14:paraId="38CD6638" w14:textId="77777777" w:rsidR="00AF1C67" w:rsidRDefault="00AF1C67" w:rsidP="00AF1C67"/><w:p w14:paraId="3AD746DD" w14:textId="77777777" w:rsidR="00AF1C67" w:rsidRDefault="00AF1C67" w:rsidP="00AF1C67"><w:r><w:t>Si s’observa que falla algun mecanisme, s’ha de parar el servei i avisar l’encarregat del manteniment.</w:t></w:r></w:p><w:p w14:paraId="6FC661ED" w14:textId="77777777" w:rsidR="00AF1C67" w:rsidRDefault="00AF1C67" w:rsidP="00AF1C67"/><w:p w14:paraId="28A7B28A" w14:textId="77777777" w:rsidR="00AF1C67" w:rsidRDefault="00AF1C67" w:rsidP="00AF1C67"><w:r><w:t>Els ascensors no es poden utilitzar com a muntacàrregues.</w:t></w:r></w:p><w:p w14:paraId="44793246" w14:textId="77777777" w:rsidR="00AF1C67" w:rsidRDefault="00AF1C67" w:rsidP="00AF1C67"/><w:p w14:paraId="788A6E9C" w14:textId="77777777" w:rsidR="00AF1C67" w:rsidRDefault="00AF1C67" w:rsidP="00AF1C67"><w:r><w:t>No es pot fumar dins de la cabina de l’ascensor.</w:t></w:r></w:p><w:p w14:paraId="71C245F0" w14:textId="77777777" w:rsidR="00AF1C67" w:rsidRDefault="00AF1C67" w:rsidP="00AF1C67"/><w:p w14:paraId="4501FE59" w14:textId="77777777" w:rsidR="00AF1C67" w:rsidRDefault="00AF1C67" w:rsidP="00AF1C67"><w:r><w:t>El conserge, porter, president de la comunitat o a qui el president delegui, és responsable del seguiment d’aquestes recomanacions d’ús.</w:t></w:r></w:p><w:p w14:paraId="78878767" w14:textId="77777777" w:rsidR="00AF1C67" w:rsidRDefault="00AF1C67" w:rsidP="00AF1C67"/><w:p w14:paraId="026269EA" w14:textId="77777777" w:rsidR="00AF1C67" w:rsidRDefault="00AF1C67" w:rsidP="00AF1C67"><w:r><w:t>En cas d’avaria, de manca de subministrament elèctric, etc. és obligatori tenir connectada la cabina amb l’empresa de manteniment.</w:t></w:r></w:p><w:p w14:paraId="1485F447" w14:textId="77777777" w:rsidR="00AF1C67" w:rsidRDefault="00AF1C67" w:rsidP="00AF1C67"/><w:p w14:paraId="71F8F918" w14:textId="77777777" w:rsidR="00AF1C67" w:rsidRPr="00F55233" w:rsidRDefault="00AF1C67" w:rsidP="00AF1C67"><w:pPr><w:rPr><w:bCs/></w:rPr></w:pPr><w:r w:rsidRPr="00F55233"><w:rPr><w:bCs/></w:rPr><w:t>Persones atrapades a l’interior per una avaria</w:t></w:r></w:p><w:p w14:paraId="7779127D" w14:textId="77777777" w:rsidR="00AF1C67" w:rsidRDefault="00AF1C67" w:rsidP="00AF1C67"><w:r><w:t>Si alguna persona queda atrapada a l’interior de la cabina a causa d’una avaria, és fonamental no perdre la calma. Cal prémer el timbre d’alarma de la botonera de cabina o comunicar el fet per telèfon, si se’n disposa d’un. Mai s’ha d’intentar obrir les portes manualment per a sortir de la cabina.</w:t></w:r></w:p><w:p w14:paraId="7AC74084" w14:textId="77777777" w:rsidR="00AF1C67" w:rsidRDefault="00AF1C67" w:rsidP="00AF1C67"/><w:p w14:paraId="4902F293" w14:textId="77777777" w:rsidR="00AF1C67" w:rsidRDefault="00AF1C67" w:rsidP="00AF1C67"><w:r><w:t>Si l’avaria és un tall de subministrament elèctric, la cabina s’aturarà i s’encendrà el llum d’emergència. Cal recordar que en aquest supòsit l’ascensor és completament segur. Si en cas d’avaria no funciona el timbre d’alarma, cal cridar periòdicament perquè se sàpiga la presència d’una persona en la cabina. Cap persona atrapada a l’interior d’una cabina ha d’intentar sortir-ne forçant les portes, i encara menys quan l’ascensor s’hagi aturat entre dues plantes.</w:t></w:r></w:p><w:p w14:paraId="1120DD87" w14:textId="77777777" w:rsidR="00AF1C67" w:rsidRDefault="00AF1C67" w:rsidP="00AF1C67"/><w:p w14:paraId="34DF3BA4" w14:textId="77777777" w:rsidR="00AF1C67" w:rsidRDefault="00AF1C67" w:rsidP="00AF1C67"><w:r><w:t>Les persones que estan fora de l’ascensor no han de manipular cap mecanisme ni cap porta. Es limitaran a avisar a la empresa de manteniment o, en un cas greu, als bombers. Si és possible, convé informar del que passa a la persona atrapada vigilant que mantingui la calma.</w:t></w:r></w:p><w:p w14:paraId="72FFD3DF" w14:textId="77777777" w:rsidR="00500DE7" w:rsidRDefault="00500DE7"><w:bookmarkStart w:id="0" w:name="_GoBack"/><w:bookmarkEnd w:id="0"/></w:p><w:p></w:p><w:p w14:paraId="5CB52117" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions de manteniment</w:t></w:r></w:p><w:p w14:paraId="2BC35CC1" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr></w:p>'
  	when 'telecomunicacions'
  		return '<w:p w14:paraId="3869DA68" w14:textId="77777777" w:rsidR="002B3380" w:rsidRPr="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions d’ús</w:t></w:r></w:p><w:p w14:paraId="4FEC8DD7" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"/><w:p></w:p><w:p w14:paraId="5CB52117" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions de manteniment</w:t></w:r></w:p><w:p w14:paraId="2BC35CC1" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr></w:p>'
  	when 'especials'
  		return '<w:p w14:paraId="3869DA68" w14:textId="77777777" w:rsidR="002B3380" w:rsidRPr="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"><w:pPr><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions d’ús</w:t></w:r></w:p><w:p w14:paraId="4FEC8DD7" w14:textId="77777777" w:rsidR="002B3380" w:rsidRDefault="002B3380" w:rsidP="002B3380"/><w:p></w:p><w:p w14:paraId="5CB52117" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr><w:r><w:rPr><w:b/></w:rPr><w:t>Instruccions de manteniment</w:t></w:r></w:p><w:p w14:paraId="2BC35CC1" w14:textId="77777777" w:rsidP="00A80AC4" w:rsidR="00A80AC4" w:rsidRDefault="00A80AC4"><w:pPr><w:widowControl/><w:tabs><w:tab w:pos="0" w:val="num"/></w:tabs><w:rPr><w:b/></w:rPr></w:pPr></w:p>'
  	end
  end

  def operacions(sistema)
    referencies = Referencia.where(:edifici_id => @edifici.id, :sistema => sistema)
    llistat_operacions = ''
    referencies.each do |referencia|
      operacio = Operacio.find(referencia.operacio_id)
