  def calendari
    any_inici = Time.now.year + 1
    any_fi = any_inici + 20
    any = any_inici
    llistat_operacions = ''
    until any == any_fi  do
    	referencies_calendari = ReferenciaCalendari.where(:edifici_id => @edifici.id, :any => any)
    	llistat_operacions = llistat_operacions + '<w:p w14:paraId="09C61C5B" w14:textId="67B32F3E" w:rsidR="0036717D" w:rsidRDefault="00085DD3" w:rsidP="00085DD3"><w:pPr><w:pStyle w:val="Heading2"/><w:rPr><w:lang w:val="es-ES"/></w:rPr></w:pPr><w:r><w:rPr><w:lang w:val="es-ES"/></w:rPr><w:t>' + any.to_s + '. Full de control anual de manteniment</w:t></w:r></w:p><w:p w14:paraId="576F6644" w14:textId="77777777" w:rsidR="00085DD3" w:rsidRPr="00085DD3" w:rsidRDefault="00085DD3" w:rsidP="00085DD3"><w:bookmarkStart w:id="0" w:name="_GoBack"/><w:bookmarkEnd w:id="0"/></w:p><w:p w14:paraId="74DC05CB" w14:textId="77777777" w:rsidR="00085DD3" w:rsidRPr="00085DD3" w:rsidRDefault="00085DD3" w:rsidP="00085DD3"/>'
